    ~ table_filtrée$longitude,
    ~ table_filtrée$latitude,
    popup = paste(table_filtrée$name, table_filtrée$location, sep = "<br>"),
    icon = icon
  ) %>%  addPopups(2.347324,
                   48.85835,
                   "Châtelet",
                   options = popupOptions(closeButton = FALSE))
